mapper.insert:
        insert into  construct_project_workteam 
        (construct_project_workTeam_id,construct_project_workTeam_category,construct_project_workTeam_userId,construct_project_workTeam_amount,
        construct_project_workTeam_departmentId,construct_project_workTeam_price,construct_project_workTeam_projectId
        ) 
        VALUES  
	        (
	        #{construct_project_workTeam_id},#{construct_project_workTeam_category},#{construct_project_workTeam_userId},#{construct_project_workTeam_amount},
	         #{construct_project_workTeam_departmentId},#{construct_project_workTeam_price},#{construct_project_workTeam_projectId}
	        )  
	        on duplicate key update construct_project_workTeam_id=#{construct_project_workTeam_id},construct_project_workTeam_category=#{construct_project_workTeam_category},
	    						construct_project_workTeam_userId=#{construct_project_workTeam_userId},construct_project_workTeam_amount=#{construct_project_workTeam_amount},
	    						construct_project_workTeam_amount=#{construct_project_workTeam_amount},construct_project_workTeam_departmentId=#{construct_project_workTeam_departmentId},
	    						construct_project_workTeam_price=#{construct_project_workTeam_price},construct_project_workTeam_projectId=#{construct_project_workTeam_projectId}

mapper.select:
SELECT * FROM  construct_project_workteam team
left join uc_user user on team.construct_project_workTeam_userId=user.userid
where team.construct_project_workTeam_projectId=${construct_project_id}
